<?php

namespace Utilisateurs\UtilisateursBundle\Repository;

/**
 * InventaireRRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class InventaireRRepository extends \Doctrine\ORM\EntityRepository
{
    public function findInventaireR($id)
    {
        $query=$this->getEntityManager()->createQuery("SELECT i FROM UtilisateursUtilisateursBundle:InventaireR i 
WHERE i.partenaire=$id");
        return $query->getResult();
    }
    public function sum()
    {
        $query=$this->getEntityManager()->createQuery("SELECT SUM( 'montant')  FROM UtilisateursUtilisateursBundle:InventaireR 
");
        return $query->getResult();
    }

    public function findInventaireNonPaye()
    {
        $query=$this->getEntityManager()->createQuery("SELECT i FROM UtilisateursUtilisateursBundle:InventaireR i 
WHERE i.done=0");
        return $query->getResult();
    }
    public function findInventairePaye()
    {
        $query=$this->getEntityManager()->createQuery("SELECT i FROM UtilisateursUtilisateursBundle:InventaireR i 
WHERE i.done=1");
        return $query->getResult();
    }

    public function findPartenaireNonpaye($id)
    {
        $query=$this->getEntityManager()->createQuery("SELECT i FROM UtilisateursUtilisateursBundle:InventaireR i 
WHERE i.partenaire=$id AND i.done=0 ");
        return $query->getResult();
    }
}
